import React from 'react';
import{Card,  CardColumns} from 'react-bootstrap';
import {Button} from 'react-bootstrap';

export default class Display extends React.Component{
  constructor(props)
  {
      super(props);
      this.state={
          items:[],
          isLoaded: false,

      }
  }

  componentDidMount()
  {

      fetch('/TSM/project/all')


      .then(res=>res.json())
      .then(json => { 
       this.setState({
              isLoaded:true,
              items: json,
                      })

                  });

  }  




render(){

var { isLoaded, items} = this.state;
 if(!isLoaded){
     return (<div>Server not yet connected!</div>);
 }

 else{
return(
    <div className="column-layout">

<CardColumns className="main-column" >
{items.map(item => (



<Card style={{ width: '18rem'  }}>


            <Card.Body key={item.projectId}> 
            <Card.Title> {item.projectTitle }</Card.Title>
            <Card.Text>
    {item.projectDescription}
  </Card.Text>
  <Button size="sm" variant="primary">Edit</Button> &nbsp; &nbsp;
  <Button  size="sm" variant="primary">Delete</Button>

             </Card.Body>



</Card>



))}

</CardColumns>

 </div>



);

 }


}








}
