import React from 'react';
import axios from 'axios';
import {Modal, Button} from 'react-bootstrap';


export default class Popup extends React.Component{
  state={

    projectTitle:'',

    projectDescription:'',
    startDate:'',
    endDate:'',
  }    
  onSubmit = e => {
    e.preventDefault();
    console.log(this.state);  
    axios.post('192.168.1.215:8082/TSM/project/add', this.state)
    .then(response =>{console.log(response)})
    .catch( error =>{console.log(error)})  
   } 


render(){
return(


<Modal
       {...this.props}
      size="lg"
      aria-labelledby="contained-modal-title-vcenter"
      centered
    >
      <Modal.Header closeButton>
        <Modal.Title id="contained-modal-title-vcenter">
         Create a new Project
        </Modal.Title>
      </Modal.Header>
      <Modal.Body>
<div>
      <form>





<label> Project Name :
<input placeholder='Project Name' type="text" 
     value= {this.state.value}
     onChange={ e=> this.setState({projectTitle: e.target.value})}/>
     <br/>
</label>
<label>Description
<textarea placeholder='Description' type="textarea"
     value= {this.state.value}
     onChange={ e=> this.setState({projectDescription: e.target.value})}/>
     </label>
     <br/>


<label> Start Date   :
     <input placeholder='start date' type="date"
     value= {this.state.value}
     onChange={ e=> this.setState({startDate : e.target.value})}/>
     <br/>
     </label>
     <label>  End Date   :
     <input placeholder='end sate' type="date"
     value= {this.state.value}
     onChange={ e=> this.setState({endDate : e.target.value})}/>
     </label>
     <br/>

    </form>
    </div>
      </Modal.Body>
      <Modal.Footer>

     <Button variant='danger'  onClick={e => this.onSubmit(e) }> submit</Button>

      </Modal.Footer>
    </Modal>


);


}



}
