import React from 'react';
export default class Show extends React.Component
{
constructor(props){
    super(props);
    this.state={
        items:[],
        isLoaded: false,

    }
}
ComponentDidMount(){

    fetch('https://jsonplaceholder.typicode.com/users')
    .then(res=>res.json())
    .then(json => { 
        this.setState({
            isLoaded:true,
        items: json,
        })

    });
}





}
