import React from 'react';
export default class Show extends React.Component
            {
constructor(props)
{
    super(props);
    this.state={
        items:[],
        isLoaded: false,

    }
}

componentDidMount()
{

    fetch('https://jsonplaceholder.typicode.com/users')
    .then(res=>res.json())
    .then(json => { 
     this.setState({
            isLoaded:true,
            items: json,
                    })

                });
}
   render()
{
 var { isLoaded, items} = this.state;
 if(!isLoaded){
     return (<div>loading...</div>);

 }
else{
    return (
        <div>
    <ul>
        {items.map(item => ( 
            <li key={item.id}> Name: {item.name } | Email :{item.email}</li>
        )) }
    </ul>
    <ul>
        {items.map(item => ( 
            <li key={item.id}> 
            City {item.address.city } | Zipcode :{item.address.zipcode}</li>
        )) }
    </ul>
    <table>
  <tr>
    <th>Firstname</th>
    <th>Lastname</th> 
    <th>Age</th>
  </tr>
  <tr>
    <td>Jill</td>
    <td>Smith</td> 
    <td>50</td>
  </tr>
  <tr>
    <td>Eve</td>
    <td>Jackson</td> 
    <td>94</td>
  </tr>
</table>

</div>


    );

}

}

}
