import React from 'react';
import axios from 'axios';
import {Modal, Button} from 'react-bootstrap';


export default class Popup extends React.Component{
  state={

    Email:'',
    Password:'',
  }    
  onSubmit = e => {
    e.preventDefault();
    console.log(this.state);  
    axios.post('https://jsonplaceholder.typicode.com/posts', this.state)
    .then(response =>{console.log(response)})
    .catch( error =>{console.log(error)})  
   } 


render(){
return(


<Modal
       {...this.props}
      size="lg"
      aria-labelledby="contained-modal-title-vcenter"
      centered
    >
      <Modal.Header closeButton>
        <Modal.Title id="contained-modal-title-vcenter">
          Create a new Project
        </Modal.Title>
      </Modal.Header>
      <Modal.Body>

      <form>
    <h3>This is a Employee login form</h3>

     <br/>



<input placeholder='Project Name' type="text" 
     value= {this.state.value}
     onChange={ e=> this.setState({Email: e.target.value})}/>
     <br/>


<input placeholder='Description' type="textarea"
     value= {this.state.value}
     onChange={ e=> this.setState({Password : e.target.value})}/>
     <br/>
     <input placeholder='start date' type="date"
     value= {this.state.value}
     onChange={ e=> this.setState({Password : e.target.value})}/>
     <br/>
     <input placeholder='end sate' type="date"
     value= {this.state.value}
     onChange={ e=> this.setState({Password : e.target.value})}/>
     <br/>

    </form>
      </Modal.Body>
      <Modal.Footer>

     <Button variant='danger'  onClick={e => this.onSubmit(e) }> submit</Button>
        <Button variant ='danger' onClick={this.props.onHide}>Close</Button>
      </Modal.Footer>
    </Modal>


);


}



}
