import { variableDeclaration } from '@babel/types';
export default class Show extends React.Component
            {
constructor(props)
{
    super(props);
    this.state={
        items:[],
        isLoaded: false,

    }
}

componentDidMount()
{

    fetch('https://jsonplaceholder.typicode.com/users')
    .then(res=>res.json())
    .then(json => { 
     this.setState({
            isLoaded:true,
            items: json,
                    })

                });
}
   render()
{
 var { isLoaded, items} = this.state;
 if(!isLoaded){
     return (<div>loading...</div>);

 }
else{
    return (
    <ul>
        {items.map(item => ( 
            <li key={item.id}> Name: {item.name } | Email :{item.email} |City: {item.city}</li>
        )) };
    </ul>
);

}

}

