  {
      super(props);

        const  items=[];
        for (let i = 0; i < 10; i++) {
          items.push({
              name: chance.first(),

          });
      }
      this.state = { items ,
          isLoaded: false,
        };


  }

  componentDidMount()
  {

      fetch('https://jsonplaceholder.typicode.com/users')


      .then(res=>res.json())
      .then(json => { 
       this.setState({
              isLoaded:true,
              items: json,
                      })

                  });

  }  




render(){

var { isLoaded, items} = this.state;
 if(!isLoaded){
     return (<div>loading...</div>);
 }

 else{
return(
<div>
{this.state.items.map((items , id) =>(
  <CardDeck>

<Card style={{ width: '18rem'  }}>


            <Card.Body key={id}> 
            <Card.Title>Title: {items.name }</Card.Title>
            <Card.Text>
    {items.email}
  </Card.Text>
  <Button size="sm" variant="primary">Edit</Button> &nbsp; &nbsp;
  <Button  size="sm" variant="primary">Delete</Button>

             </Card.Body>



</Card>

</CardDeck>

))}



</div>



);

 }


}








